mapper.select:
select * from USER where 1=1

		and ID = #{id}


		and FIRST_NAME = #{firstName}


		and LAST_NAME = #{lsetName}


		and EMAIL = #{email}


		and PHONE = #{phone}


		and PASSWORD = #{password}


		and SALT = #{salt}


		and DELETE_AT = null


		and DELETE_AT = #{deleteAt}

mapper.update:
		update USER 
		
 			 FIRST_NAME = #{firstName}
 			 LAST_NAME = #{lsetName}
 			 EMAIL = #{email}
 			 PHONE = #{phone}
 			 PASSWORD = #{password}
 			 SALT = #{salt}
 			 UPDATE_AT = #{updateAt}
 			 DELETE_AT = #{deleteAt}
			
		where ID = #{id}
